<?phpheader ("content-type: text/xml");function file_get_contents_safe($url){				if (is_numeric(strpos($url,"http://")))		{			$url=str_replace("..","",$url);							if (get_headers($url))			{				$url=str_replace("http://","",$url);									$content=file_get_contents("http://".$url);									return $content;			}		}	}$g=file_get_contents_safe("http://maps.googleapis.com/maps/api/geocode/xml?address=".urlencode($_GET["address"])."&sensor=false&language=en");print_r($g);?>