<?php/*version message*/include("../../../php_source/proxies/required.php");class thematicLayersCreateXML{	var $output;		var $host="http://localhost/getsdiportalv3/";	function thematicLayersCreateXML($data)	{		session_start();		$sid=session_id();			$timestamp=strtotime("now");			$filename="tmp_uploads/".$sid."/".$timestamp.".xml";				if (!is_dir("../tmp_uploads/".$sid))		{			mkdir("../tmp_uploads/".$sid, 0777);		}		$fWrite = fopen("../".$filename, 'wr');				$wrote = fwrite($fWrite, $data["sld"]);				fclose($fWrite);				$this->output=$this->host."modules/thematicLayers/".$filename;				session_destroy(); 	}	}$sld=new thematicLayersCreateXML($_POST);echo json_encode($sld->output);?>