<?php/*version message*/include("required.php");if (isset($_REQUEST["nocache"])){	$seconds_to_cache= -1000;}else{	$seconds_to_cache = 3600;}$ts = gmdate("D, d M Y H:i:s", time() + $seconds_to_cache) . " GMT";header("Expires: $ts");header("Pragma: cache");header("Cache-Control: max-age=$seconds_to_cache");header('Content-type: application/xml');$url=$_SERVER["REQUEST_URI"];$url=explode("url=",$url);$url=$url[1];$url=create_authentication_url($url,$_GET['serviceUsername'],$_GET['servicePassword']);$kml=file_get_contents_safe($url,false);echo $kml;?>